import React, { useState, useMemo }from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import WelcomePage from "./Pages/WelcomePage";
import MainPage from "./Pages/MainPage";
import CityPage from "./Pages/CityPage";
import NotFound from "./Pages/NotFound";
const App = () => {
  const [allWeather, setAllWeather] = useState({})

  const onSetAllWeather = useMemo(() => (weatherCity) => {setAllWeather(allWeather => ({...allWeather, ...weatherCity}))}, [setAllWeather])
  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <WelcomePage></WelcomePage>
        </Route>
        <Route path="/main">
          <MainPage allWeather={allWeather} onSetAllWeather={onSetAllWeather}></MainPage>
        </Route>
        <Route path="/city/:countryCode/:city">
          <CityPage allWeather={allWeather} onSetAllWeather={onSetAllWeather}></CityPage>
        </Route>
        <Route>
          <NotFound></NotFound>
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
