import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useForm } from "react-hook-form";
const Search = ({ callback }) => {
  const [newCity, setNewCity] = useState("");
  const [newCountryCode, setNewCountryCode] = useState("");
  const { register, errors, handleSubmit } = useForm();
  const updateCity = (e) => setNewCity(e.target.value);
  const updateCountryCode = (e) => setNewCountryCode(e.target.value);
  const onSubmit = (e) => {
    e.preventDefault();
    callback(newCity, newCountryCode);
    setNewCountryCode("");
    setNewCity("");
  };

  /*const createNewCity = (e) => {


  };*/

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="searchForm">
      <TextField
        variant="outlined"
        label="City"
        color="primary"
        className="formItem"
        value={newCity}
        onChange={updateCity}
        name="city"
        ref={register({
          required: { value: true, message: "This field is required" },
        })}
        required
      ></TextField>
      <TextField
        variant="outlined"
        label="Country Code"
        color="primary"
        className="formItem"
        value={newCountryCode}
        onChange={updateCountryCode}
        name="countryCode"
        ref={register({
          required: { value: true, message: "This field is required" },
        })}
        required
      ></TextField>
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className="formItem"
      >
        Search
      </Button>
    </form>
  );
};

export default Search;
