import React, { useState, useCallback, useMemo } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import WelcomePage from "./Pages/WelcomePage";
import MainPage from "./Pages/MainPage";
import CityPage from "./Pages/CityPage";
import NotFound from "./Pages/NotFound";
const App = () => {
  const [allWeather, setAllWeather] = useState({});
  const [allChartData, setAllChartData] = useState(null);
  const [allForecastItemList, setAllForecastItemList] = useState(null);
  const onSetAllWeather = useCallback(
    (weatherCity) => {
      setAllWeather((allWeather) => {
        return { ...allWeather, ...weatherCity };
      });
    },
    [setAllWeather]
  );
  const onSetAllChartData = useCallback((chartDataCity) => {
    setAllChartData((allChartData) => {
      return { ...allChartData, ...chartDataCity };
    });
  },[setAllChartData]);
  const onSetAllForecastItemList = useCallback((forecastItemListCity) => {
    setAllForecastItemList((allForecastItemList) => {
      return { ...allForecastItemList, ...forecastItemListCity };
    });
  },[setAllForecastItemList]);
  const actions = useMemo(
    () => ({
      onSetAllWeather,
      onSetAllChartData,
      onSetAllForecastItemList,
    }),
    [onSetAllWeather, onSetAllChartData, onSetAllForecastItemList]
  );
  const data = useMemo(
    () => ({
      allWeather,
      allChartData,
      allForecastItemList,
    }),
    [allWeather, allChartData, allForecastItemList]
  );

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <WelcomePage />
        </Route>
        <Route path="/main">
          <MainPage data={data} actions={actions} />
        </Route>
        <Route path="/city/:countryCode/:city">
          <CityPage data={data} actions={actions} />
        </Route>
        <Route>
          <NotFound />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
