import React from "react";
import PropTypes from "prop-types";
import {
    WiCloud,
    WiDayCloudy,
    WiDayFog,
    WiDaySunny,
    WiRain,
  } from "react-icons/wi";
  import { IconContext } from "react-icons";

  const validValues = ["cloud", "cloud", "fog", "sunny", "rain"];

  const stateByName = {
    cloud: WiCloud,
    cloudy: WiDayCloudy,
    fog: WiDayFog,
    sunny: WiDaySunny,
    rain: WiRain,
  };
  const renderState = (state) => {
    const Icon = stateByName[state];
    return <Icon />;
  };


const IconState = ({state}) => {
  return (
    <>
      <IconContext.Provider value={{ size: "5em" }}>
        {renderState(state)}
      </IconContext.Provider>
    </>
  );
};

IconState.propTypes = {};

export default IconState;
