import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types'
import axios from 'axios'
import Grid from '@material-ui/core/Grid'
import List from '@material-ui/core/List'
import ListItem from '@material-ui/core/ListItem'
import CityInfo from './../CityInfo'
import Weather from './../Weather'

const renderCityAndCountry = (eventOnClickCity) => (cityCountry, weather) => {
  const { city, country } = cityCountry;
  return (
    <ListItem button key={city} onClick={eventOnClickCity}>
      <Grid container justify="center" alignItems="center">
        <Grid item md={9} xs={12}>
          <CityInfo city={city} country={country} />
        </Grid>
        <Grid item md={3} xs={12}>
          {weather ? (
            <Weather temperature={weather.temperature} state={weather.state} />
          ) : (
            "No Data"
          )}
        </Grid>
      </Grid>
    </ListItem>
  );
};

const CityList = ({ cities, onClickCity }) => {
  const [allWeather, setAllWeather] = useState({});
  useEffect(() => {
    const setWeather = (city, country) => {
      const apiKey = "6eed5ecf1b61d636500feff49314d96a";
      const url = `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`;
      axios.get(url).then((response) => {
        const { data } = response;
        const temperature = data.main.temp;
        const state = "sunny";
        const propName = `${city}-${country}`
        const propValue = { temperature, state }
        setAllWeather(allWeather => ({ ...allWeather, [propName]: {propValue}}));
      });
    };
    cities.forEach(({ city, country }) => {
      setWeather(city, country);
    });
  }, [cities]);

  return (
    <List>
      {cities.map((cityAndCountry) =>
        renderCityAndCountry(onClickCity)(
          cityAndCountry,
          allWeather[`${cityAndCountry.city}-${cityAndCountry.country}`]
        )
      )}
    </List>
  );
};

CityList.propTypes = {
  cities: PropTypes.arrayOf(
    PropTypes.shape({
      city: PropTypes.string.isRequired,
      country: PropTypes.string.isRequired,
    })
  ).isRequired,
  onClickCity: PropTypes.func.isRequired,
};

export default CityList;
