import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useForm } from "react-hook-form";
const Search = ({ callback }) => {
  const [newCity, setNewCity] = useState("");
  const [newCountryCode, setNewCountryCode] = useState("");
  const { register, errors, handleSubmit, reset } = useForm();
  const updateCity = (e) => setNewCity(e.target.defaultValue);
  const updateCountryCode = (e) => setNewCountryCode(e.target.defaultValue);

  const onSubmit = (e) => {
    reset({ city: "" })
    callback(newCity, newCountryCode);
    setNewCountryCode("");
    setNewCity("");
  };
  /*const createNewCity = (e) => {};*/

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="searchForm">
      <TextField

        variant="outlined"
        label="City"
        color="primary"
        className="formItem"
        defaultValue={newCity}
        onChange={updateCity}
        name="city"
        {...register("city", {
          required: { value: true, message: "This field is required" },
        })}
        required
      />
      <span className="text-danger text-small d-block mb-2">
        {errors?.city?.message}
      </span>
      <TextField
        variant="outlined"
        label="Country Code"
        color="primary"
        className="formItem"
        defaultValue={newCountryCode}
        onChange={updateCountryCode}
        name="countryCode"
        {...register("countryCode", {
          required: { value: true, message: "This field is required" },
          maxLength: {
            value: 2,
            message: "No more that 2 characters",
          },
        })}
        required
      />
      <span className="text-danger text-small d-block mb-2">
        {errors?.countryCode?.message}
      </span>
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className="formItem"
        //onClick={createNewCity}
      >
        Search
      </Button>
    </form>
  );
};

export default Search;
