import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

const Search = ({ callback }) => {
  const [newCity, setNewCity] = useState("");
  const [newCountryCode, setNewCountryCode] = useState("");

  const updateCity = (e) => setNewCity(e.target.value);
  const updateCountryCode = (e) => setNewCountryCode(e.target.value);
  const createNewCity = (e) => {
    console.log(newCity, newCountryCode);
    e.preventDefault();
    callback(newCity, newCountryCode);
    setNewCity("");
    setNewCountryCode("")
  };

  return (
    <div className="searchForm" >
      <TextField
        variant="outlined"
        label="City"
        color="primary"
        className="formItem"
        onChange={updateCity}
      ></TextField>
      <TextField
        variant="outlined"
        label="Country Code"
        color="primary"
        className="formItem"
        onChange={updateCountryCode}
      ></TextField>
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className="formItem"
        onClick={createNewCity}
      >
        Search
      </Button>
    </div>
  );
};

export default Search;
