import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import Paper from "@material-ui/core/Paper";
import CityList from "../Components/CityList";
import AppFrame from "../Components/AppFrame";
import { getCities } from "../Utilities/serviceCities";
import Search from "../Components/Search";

const MainPage = ({ actions, data }) => {
  const history = useHistory();

  const onClickHandler = (city, countryCode) => {
    history.push(`/city/${countryCode}/${city}`);
  };
  const [city, setCity] = useState([]);
  const [countryCode, setCountryCode] = useState([]);
  const [cityCountry, setCityCountry] = useState([]);

  const createNewTask = (newCity, newCountryCode) => {
    console.log(newCity, newCountryCode);
    setCity([...city, newCity]);
    console.log(city);
    setCountryCode([...countryCode, newCountryCode]);
    console.log(countryCode);
    console.log(city, countryCode);
    setCityCountry(() => {
      return {
        city,
        countryCode,
      };
    });
  };
  return (
    <AppFrame>
      <Search callback={createNewTask} />
      <Paper elevation={3}>
        <CityList
          data={data}
          actions={actions}
          cities={getCities()}
          onClickCity={onClickHandler}
        />
      </Paper>
    </AppFrame>
  );
};

export default MainPage;
