const cities = [
    { city: "Buenos Aires",countryCode: "AR" },
    { city: "Bogotá", countryCode: "CO" },
    { city: "Madrid", countryCode: "ES" },
    { city: "Ciudad de México", countryCode: "MX" },
  ];

  export const getCities = () => (cities)
  export const getCountry = (countryCode) => cities.filter(c => c.countryCode === countryCode)[0].country
