import  { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import moment from "moment";
import axios from "axios";
import { toCelsius } from "../Utilities/utilities";
import { getForecastUrl } from "../Utilities/urls";


const useCityPage = () => {
    const [chartData, setChartData] = useState(null);
    const [forecastItemList, setForecastItemList] = useState(null);
    const { city, countryCode } = useParams();
    useEffect(() => {
      const getForecast = async () => {

        const url = getForecastUrl(city, countryCode)

        try {
          const { data } = await axios.get(url);
          console.log("data", data);

          const daysAhead = [0, 1, 2, 3, 4, 5];
          const days = daysAhead.map((d) => moment().add(d, "d"));
          const dataAux = days.map((day) => {
            const tempObjArray = data.list.filter((item) => {
              const dayOfYear = moment.unix(item.dt).dayOfYear();
              return dayOfYear === day.dayOfYear();
            });
            console.log("dia", day.dayOfYear());
            console.log(tempObjArray);
            const temps = tempObjArray.map((item) => item.main.temp);

            return {
              dayHour: day.format("dddd"),
              min: Number(toCelsius(Math.min(...temps)).toFixed(0)),
              max: Number(toCelsius(Math.max(...temps)).toFixed(0)),
              hasTemps: (temps.length > 0 ?  true:false  )
            };
          }).filter(item => item.hastTemps = true);

          setChartData(dataAux);

          const interval = [3, 6, 9, 12, 15, 18];

          const forecastItemListAux = data.list
            .filter((item, index) => interval.includes(index))
            .map((item) => {
              return {
                hour: moment.unix(item.dt).hour(),
                weekDay: moment.unix(item.dt).format("dddd"),
                state: item.weather[0].main.toLowerCase(),
                temperature: Number(toCelsius(item.main.temp).toFixed(0)),
              };
            });
          setForecastItemList(forecastItemListAux);
        } catch (error) {
          console.log("Error");
        }
      };

      getForecast();
    }, [city, countryCode]);
    return {city, chartData, forecastItemList}
}

export default useCityPage
