import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { searchSchema } from "../../Utilities/searchValidation";
const Search = ({ callback }) => {
  const [newCity, setNewCity] = useState("");
  const [newCountryCode, setNewCountryCode] = useState("");
  const updateCity = (e) => setNewCity(e.target.defaultValue);
  const updateCountryCode = (e) => setNewCountryCode(e.target.defaultValue);

  const handleSubmit = (e) => {
    e.preventDefault()
    let searchData = {
      city: newCity,
      countryCode: newCountryCode
    }
    callback(newCity, newCountryCode);
    console.log(newCountryCode, newCity);
    setNewCountryCode("");
    setNewCity("");
  };

  return (
    <form onSubmit={handleSubmit()} className="searchForm">
      <TextField
        variant="outlined"
        label="City"
        color="primary"
        className="formItem"
        value={newCity}
        onChange={updateCity}
        name="city"
        required
      />

      <TextField
        variant="outlined"
        label="Country Code"
        color="primary"
        className="formItem"
        value={newCountryCode}
        onChange={updateCountryCode}
        name="countryCode"
        required
      />

      <span className="text-danger text-small d-block mb-2">
        {errors?.countryCode?.message}
      </span>
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className="formItem"
        //onClick={createNewCity}
      >
        Search
      </Button>
    </form>
  );
};

export default Search;
