import React, { Component } from "react";

class ErrorBoundary extends Component {

    isActive = () => {
        return this.props.active ? " Active" : " Not Active"
    }

    render() {
    return (
        <h1>
            Error Boundary{this.props.saludo}
            {
                this.isActive()
            }
        </h1>
    )
  }
}

export default ErrorBoundary;
