import React, { useState, useCallback } from "react";
import { useHistory } from "react-router-dom";
import Paper from "@material-ui/core/Paper";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import CityList from "../CityList";

const Search = () => {
  const history = useHistory();
  const onClickHandler = useCallback(
    (city, countryCode) => {
      history.push(`/city/${countryCode}/${city}`);
    },
    [history]
  );
  const [city, setCity] = useState("Bogotá");
  const [countryCode, setCountryCode] = useState("CO");
  const handleSubmit = (e) => {
    e.preventDefault();
    setCity()
    setCountryCode()

  };
  const cities = (city, countryCode) => {
    const cities = [];
    cities.push({ city: `${city}`, countryCode: `${ countryCode }` });
    console.log(cities);
    return cities;
  };
  return (
    <div>
      <form
        noValidate
        autoComplete="off"
        className="searchForm"
        onSubmit={handleSubmit}
      >
        <TextField
          variant="outlined"
          label="City"
          className="formItem"
          required
        />
        <TextField
          variant="outlined"
          label="Country Code"
          className="formItem"
        />
        <Button
          type="submit"
          variant="contained"
          color="primary"
          className="formItem"
        >
          Search
        </Button>
      </form>
      <Paper elevation={10}>
        <CityList cities={cities(city, countryCode)} onClickCity={onClickHandler} />
      </Paper>
    </div>
  );
};

export default Search;
