import React, { useState, useEffect } from "react";
import axios from "axios";
import convertUnits from "convert-units"
import Grid from "@material-ui/core/Grid";
import moment from "moment";
import { useParams } from "react-router-dom";
import CityInfo from "../Components/CityInfo";
import Weather from "../Components/Weather";
import WeatherDetails from "../Components/WeatherDetails";
import ForecastChart from "../Components/ForecastChart";
import Forecast from "../Components/Forecast";
import AppFrame from "../Components/AppFrame";

const dataExample = [
  {
    dayHour: "Jue 18",
    min: 14,
    max: 22,
  },
  {
    dayHour: "Vie 06",
    min: 18,
    max: 27,
  },
  {
    dayHour: "Vie 12",
    min: 18,
    max: 28,
  },
  {
    dayHour: "Vie 18",
    min: 18,
    max: 25,
  },
  {
    dayHour: "Sab 06",
    min: 15,
    max: 22,
  },
  {
    dayHour: "Sab 12",
    min: 12,
    max: 19,
  },
];

const forecastItemListExample = [
  { hour: 18, state: "clear", temperature: 17, weekDay: "Jueves" },
  { hour: 6, state: "clouds", temperature: 18, weekDay: "Viernes" },
  { hour: 12, state: "drizzle", temperature: 18, weekDay: "Viernes" },
  { hour: 18, state: "clouds", temperature: 19, weekDay: "Viernes" },
  { hour: 6, state: "rain", temperature: 17, weekDay: "S치bado" },
  { hour: 12, state: "rain", temperature: 17, weekDay: "S치bado" },
];

const CityPage = () => {
  const [data, setData] = useState(null);
  const [forecastItemList, setForecastItemList] = useState(null);
  const { city, countryCode } = useParams();
  useEffect(() => {
    const getForecast = async () => {
      const apiKey = "6eed5ecf1b61d636500feff49314d96a";
      const url = `http://api.openweathermap.org/data/2.5/forecast?q=${city},${countryCode}&appid=${apiKey}`;

      try {
        const { data } = await axios.get(url);
        console.log("data",data);

        const daysAhead = [ 0, 1, 2, 3, 4, 5 ]
        const days = daysAhead.map(d => moment().add(d, 'd'))
        const dataAux = days.map(day => {

            const tempObjArray = data.list.filter(item => {
              const dayOfYear = moment.unix(item.dt).dayOfYear()
              return dayOfYear === day.dayOfYear()
            })
            console.log('dia',day.dayOfYear())
            console.log(tempObjArray)
            const temps = tempObjArray.map(item => item.main.temp)
            const toCelsius = (temp) => convertUnits(temp).from('K').to('C')

            return ({
              dayHour: day.format('dddd'),
              min: toCelsius(Math.min(...temps)).toFixed(0),
              max: toCelsius(Math.max(...temps)).toFixed(0)
            })
        })

        setData(dataAux);
        setForecastItemList(forecastItemListExample);
      } catch (error) {
        console.log("Error");
      }
    };

    getForecast()
  }, [city, countryCode]);

  const country = "Argentina";
  const state = "clouds";
  const temperature = 20;
  const humidity = 80;
  const wind = 5;
  //const data = dataExample;
  //const forecastItemList = forecastItemListExample;

  return (
    <AppFrame>
      <Grid
        container
        justifyContent="space-around"
        direction="column"
        spacing={2}
      >
        <Grid
          item
          container
          xs={12}
          justifyContent="center"
          alignItems="flex-end"
        >
          <CityInfo city={city} country={country} />
        </Grid>
        <Grid item xs={12}>
          <Weather state={state} temperature={temperature} />
          <WeatherDetails humidity={humidity} wind={wind} />
        </Grid>
        <Grid item>{data && <ForecastChart data={data} />}</Grid>
        <Grid item>
          {forecastItemList && <Forecast forecastItemList={forecastItemList} />}
        </Grid>
      </Grid>
    </AppFrame>
  );
};

export default CityPage;
