import React, { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useForm, Controller } from "react-hook-form";
const Search = ({ callback }) => {
  const [newCity, setNewCity] = useState("");
  const [newCountryCode, setNewCountryCode] = useState("");
  const { control, errors, handleSubmit } = useForm();
  const updateCity = (e) => setNewCity(e.target.defaultValue);
  const updateCountryCode = (e) => setNewCountryCode(e.target.defaultValue);

  const onSubmit = (e) => {
    callback(newCity, newCountryCode);
    console.log(newCountryCode, newCity)
    setNewCountryCode("");
    setNewCity("");
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="searchForm">
      <Controller
        control={control}
        name="city"
        defaultValue=""
        onChange={updateCity}
        render={({ newCity }) => (
          <TextField
            variant="outlined"
            label="City"
            color="primary"
            className="formItem"
            value={newCity}
            name="city"
            required
          />
        )}
      />
      <Controller
        control={control}
        name="city"
        defaultValue=""
        onChange={updateCountryCode}
        render={({ newCountryCode }) => (
          <TextField
            variant="outlined"
            label="Country Code"
            color="primary"
            className="formItem"
            value={newCountryCode}
            name="countryCode"
            required
          />
        )}
      />

      <span className="text-danger text-small d-block mb-2">
        {errors?.countryCode?.message}
      </span>
      <Button
        type="submit"
        variant="contained"
        color="primary"
        className="formItem"
        //onClick={createNewCity}
      >
        Search
      </Button>
    </form>
  );
};

export default Search;
