import { useState, useEffect } from "react";
import axios from "axios";
import convertUnits from "convert-units";
import { validValues } from "../Components/IconState";
import { getCityCode } from "../Utilities/utilities";
import { getWeatherUrl } from "../Utilities/urls";

const useCityList = (cities) => {
  const [allWeather, setAllWeather] = useState({});
  const [error, setError] = useState(null);
  useEffect(() => {
    const setWeather = async (city, countryCode) => {

      const url = getWeatherUrl(city, countryCode)

      try {
        const response = await axios.get(url);
        const { data } = response;
        const temperature = Number(
          convertUnits(data.main.temp).from("K").to("C").toFixed(0)
        );
        const stateFromServer = data.weather[0].main.toLowerCase();

        const state = validValues.includes(stateFromServer)
          ? stateFromServer
          : null;
        const propName = getCityCode(city, countryCode);
        const propValue = { temperature, state };
        setAllWeather((allWeather) => ({
          ...allWeather,
          [propName]: propValue,
        }));
      } catch (error) {
        if (error.response) {
          setError("Weather Server Error");
        } else if (error.request) {
          setError("Verify Your Connection");
        } else {
          setError("Un-expected Error");
        }
      }
    };
    cities.forEach(({ city, countryCode }) => {
      setWeather(city, countryCode);
    });
  }, [cities]);
  return { allWeather, error, setError };
};

export default useCityList;
