import React, { Component } from "react";

class ErrorBoundary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active: false,
    };
  }
  isActive = () => {
    return this.state.active ? " Active" : " Not Active";
  };
  onClickHandler = () =>{

    this.setState({ active: true})
  }
  render() {
    return (
      <div>
        <button onClick={this.onClickHandler}>Activate</button>
        <h1>
          Error Boundary{this.props.saludo}
          {this.isActive()}
        </h1>
      </div>
    );
  }
}

export default ErrorBoundary;
